import React from "react";
import "./App.css";
import { useState, useEffect } from "react";
import ChatList from "./Components/ChatList/ChatList";
import GroupModal from "./Components/GroupModal/GroupModal";

const App = () => {
  const [groups, setGroups] = useState([]);
  const [selectedGroup, setSelectedGroup] = useState(null);
  const [messages, setMessages] = useState([]);
  const [newMessage, setNewMessage] = useState("");
  const [showCreateGroupModal, setShowCreateGroupModal] = useState(false);

  useEffect(() => {
    const storedGroups = JSON.parse(localStorage.getItem("groups"));
    if (storedGroups) {
      setGroups(storedGroups);
    }
  }, []); // Load groups from local storage on component mount

  useEffect(() => {
    localStorage.setItem("groups", JSON.stringify(groups)); // Save groups to local storage whenever groups change
  }, [groups]);

  const createGroup = () => {
    setShowCreateGroupModal(true);
  };

  const handleCreateGroup = (groupName) => {
    setGroups([...groups, groupName]);
    setShowCreateGroupModal(false);
  };

  const selectGroup = (group) => {
    setSelectedGroup(group);
    setMessages([]);
  };

  const sendMessage = () => {
    if (newMessage) {
      const currentDate = new Date().toLocaleString();
      setMessages([...messages, { text: newMessage, date: currentDate }]);
      setNewMessage("");
    }
  };

  return (
    <div className="app">
      <ChatList
        selectedGroup={selectedGroup}
        messages={messages}
        newMessage={newMessage}
        sendMessage={sendMessage}
        setNewMessage={setNewMessage}
      />
      <GroupModal
        showCreateGroupModal={showCreateGroupModal}
        closeCreateGroupModal={() => setShowCreateGroupModal(false)}
        createGroup={handleCreateGroup}
        groups={groups}
        createGroups={createGroup}
        selectGroup={selectGroup}
      />
    </div>
  );
};

export default App;
