import React, { useRef, useState } from "react";
import AddIcon from "@mui/icons-material/Add";
import "./GroupModal.css";
function CreateGroupModal({
  showCreateGroupModal,

  createGroup,
  groups,
  createGroups,
  selectGroup,
}) {
  const groupNameRef = useRef(null);
  const [selectedColor, setSelectedColor] = useState("#007bff");
  const colorOptions = ["#007bff", "#ff5722", "#4caf50", "#e91e63", "#ffc107"];

  const handleCreateGroup = () => {
    const groupName = groupNameRef.current.value;
    if (groupName) {
      createGroup(groupName, selectedColor);

      groupNameRef.current.value = "";
      setSelectedColor("#007bff");
    }
  };
  const colorButtons = colorOptions.map((color, index) => (
    <button
      key={index}
      className={`color-button ${color === selectedColor ? "selected" : ""}`}
      style={{ backgroundColor: color }}
      onClick={() => setSelectedColor(color)}
    ></button>
  ));

  return (
    <div className="_container">
      <div className="group-Modal">
        <button className="btn-grp" onClick={createGroups}>
          <AddIcon></AddIcon>
          Create Notes Group
        </button>
        <ul className="group-list">
          {groups.map((group, index) => (
            <li
              className="group-key"
              key={index}
              onClick={() => selectGroup(group)}
            >
              {group}
            </li>
          ))}
        </ul>
        {showCreateGroupModal && (
          <div className="modal">
            <div className="modal-content">
              <span>Create New Notes Group</span>
              <input type="text" ref={groupNameRef} placeholder="Group Name" />
              <label htmlFor="colorPicker">Select Color:</label>
              <div className="color-picker">{colorButtons}</div>
              <button onClick={handleCreateGroup}>Create</button>
            </div>
          </div>
        )}
      </div>
    </div>
  );
}

export default CreateGroupModal;
