import React, { useRef, useState, useEffect } from "react";
import AddIcon from "@mui/icons-material/Add";
import "./GroupModal.css";
function CreateGroupModal({
  showCreateGroupModal,

  createGroup,
  groups,
  createGroups,
  selectGroup,
}) {
  const groupNameRef = useRef(null);
  const [selectedColor, setSelectedColor] = useState("#007bff");

  const [localGroups, setLocalGroups] = useState([]);

  useEffect(() => {
    const storedGroups = JSON.parse(localStorage.getItem("groups"));
    if (storedGroups) {
      setLocalGroups(storedGroups);
    }
  }, []);

  useEffect(() => {
    localStorage.setItem("groups", JSON.stringify(localGroups));
  }, [localGroups]);

  const handleCreateGroup = () => {
    const groupName = groupNameRef.current.value;
    if (groupName) {
      createGroup(groupName, selectedColor);

      groupNameRef.current.value = "";
      setSelectedColor("#007bff", "#007bfr");
    }
  };

  return (
    <div className="_container">
      <div className="group-Modal">
        <button className="btn-grp" onClick={createGroups}>
          <AddIcon></AddIcon>
          Create Notes Group
        </button>
        <ul>
          {groups.map((group, index) => (
            <li key={index} onClick={() => selectGroup(group)}>
              {group}
            </li>
          ))}
        </ul>
        {showCreateGroupModal && (
          <div className="modal">
            <div className="modal-content">
              <span>Create New Notes Group</span>
              <input type="text" ref={groupNameRef} placeholder="Group Name" />
              <label htmlFor="colorPicker">Select Color:</label>
              <input
                type="color"
                id="colorPicker"
                value={selectedColor}
                onChange={(e) => setSelectedColor(e.target.value)}
              />
              <button onClick={handleCreateGroup}>Create</button>
            </div>
          </div>
        )}
      </div>
    </div>
  );
}

export default CreateGroupModal;
